INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (350, false, '/CpLAfXgSNeNRRbRzPrTuzKmIHO.jpg', 'en', 'The Devil Wears Prada', '명문대학을 졸업한 소도시 출신의 앤드리아 삭스는 저널리스트 꿈을 안고 뉴욕에 상경한다. 나름 대학에서 편집장도 하고 상도 받았던 그녀는 자신감에 차 뉴욕의 여러 언론사에 이력서를 넣지만 결과는 처참했다. 결국 그의 이력서에 답한 곳은 오직 한 곳 런웨이 뿐이다. 런웨이는 세계 최고의 패션지로 누구나 입사하기를 열망하는 곳이지만 앤드리아에게는 탐탁치 않는 자리. 그도 그럴 것이 저널리스트를 꿈꾸는 그녀가 면접을 볼 자리는 편집장의 비서직이었던 것이다. 앤드리아는 패션의 ‘패’자도 모르고 런웨이가 어떤 잡지인지, 그곳의 편집장이 얼마나 패션계에서 유명한 사람인지 전혀 모르지만 뉴욕에서 꼭 성공하겠다는 열정 하나로 당당하게 면접을 보러 간다. 그런데…!!!  면접 날부터 앤드리아는 온갖 무시를 당한다. 그녀의 옷차림새는 런웨이의 패셔너블한 패셔니스타들 사이에서 독보적으로 눈에 띌 만큼 촌스러웠다. 그리고 그녀가 모셔야(!) 할 편집장 미란다 프리슬리는 그야말로 지옥에서 온 악마 같은 상사! 자신과 너무나 틀린 이곳에 입사하는 것을 포기한 앤드리아. 그런데 그때 그녀는 합격했다는 말을 듣는다. 앤드리아는 온통 첫 직장생활의 꿈에 부풀어 남자친구 네이트와 친구들에게 일장 자랑을 늘어놓는다. 하지만 지옥 같은 현실이 곧 그녀 앞에 놓여있었으니…', 110.068, '/ySp5RmSAMzw8KCeH94cCaPPfhLu.jpg', '2006-06-29', '악마는 프라다를 입는다', false, 7.397, 11815);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (389, false, '/qqHQsStV6exghCM7zbObuYBiYxw.jpg', 'en', '12 Angry Men', '뉴욕시의 법정에 아버지를 칼로 찌른 한 소년의 살인혐의를 두고, 12인의 배심원들은 만장일치 합의를 통해 소년의 유무죄 여부를 가려줄 것을 요구받는다. 판사는 유죄일 경우 이 소년은 사형이 불가피하다는 것을 이들에게 미리 일러둔다.  배심원 방에 모인 이들은 투표를 통해 유무죄 여부를 가리기로 한다. 사람들이 전부 소년이 유죄로 판단하는 가운데, 오직 한 배심원만이 소년이 무죄라고 주장하는데...', 105.469, '/xzh6Rq9cKnE1M309PzC5S5QWF9S.jpg', '1957-04-10', '12명의 성난 사람들', false, 8.543, 8423);
