INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (604, false, '/pxK1iK6anS6erGg4QePmMKbB1E7.jpg', 'en', 'The Matrix Reloaded', '시온이 컴퓨터 군단에게 장악될 위기에 처하면서, 네오는 자신의 능력에 대한 더 큰 통제력을 갖게 된다. 이제 몇 시간 후면 지구상에 남은 인류 최후의 보루인 시온이 인간 말살을 목적으로 프로그래밍 된 센티넬 무리에 의해 짓밟히게 될 터. 그러나 시온의 시민들은 오라클의 예언이 이루어져 전쟁이 끝날 것이라는 모피어스의 신념에 용기를 얻고, 네오에게 모든 희망과 기대를 걸어보기로 한다. 서로에 대한 사랑의 힘으로, 스스로에 대한 믿음으로 용기를 얻은 네오와 트리니티는 모피어스와 함께 인간을 억압하고 착취하는 시스템에 맞서기 위해 매트릭스로 돌아간다.', 97.154, '/5RsZJfcXUndClV1vaI68V2shJOs.jpg', '2003-05-15', '매트릭스 2: 리로디드', false, 7.052, 10735);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (605, false, '/533xAMhhVyjTy8hwMUFEt5TuDfR.jpg', 'en', 'The Matrix Revolutions', '진실을 찾는 여정에 한 걸음 더 접근하게 된 네오. 그러나 그 와중에 능력을 소진하고 매트릭스와 현실세계의 중간계를 떠돌게 된다. 한편 기계들이 인간말살을 목적으로 인류 최후의 보루 시온으로 침공해오자 인간들은 인류의 미래를 지키기 위해 필사적인 전투를 벌인다. 그러나 베인의 몸 속에 침투한 에이전트 스미스가 본 모습을 드러내면서 네오에게는 예기치 못한 변수가 생긴다. 매 순간 세력이 커져가면서 기계들의 통제권까지 벗어난 스미스는 현실 세계와 매트릭스는 물론 기계도시까지 말살할 야욕을 불태우고, 오라클은 네오에게 마지막 조언을 건네는데...', 70.861, '/wLS7CaUNyz4hVwCxj7mXhX66A7r.jpg', '2003-11-05', '매트릭스 3: 레볼루션', false, 6.726, 9679);
