INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (608, false, '/o1l6OCqA3pYDZHIc5L1s7CEzCMv.jpg', 'en', 'Men in Black II', '지구에 잠입한 외계인을 감시하는 MIB요원 제이. 요원 케이가 MIB를 떠난 후, 많은 파트너들과 호흡을 맞추지만 베테랑 케이의 빈자리가 크기만 하다. 또 다른 수다쟁이 요원 프랭크는 파트너라기보다 오히려 제이를 거추장스럽게 하며 말썽만 피울 뿐인데… 그러던 중 25년전, MIB에게 당한 복수를 위해 마이크로 우주선이 지구에 침입한다. 콜라캔 크기의 우주선에서 내린 외계 생명체는 여성지 모델로 자신을 복제하고 MIB에 침입한다. 그녀의 이름은 셀리나… MIB요원 중 일부가 셀리나와 손을 잡게 된 사실을 알게 되는 제이. 그러나 때는 이미 늦어 지구는 은하계의 전쟁에 휘말린 위험에 처하게 된다. 위기에 놓인 지구, 요원 제이는 요원 케이의 도움을 필요로 하게 되고 제이는 케이의 기억을 복구시키려 갖은 애를 쓰는데…', 73.865, '/5KNvkuFXB3C6xBBgDWTVDKluh4E.jpg', '2002-07-03', '맨 인 블랙 2', false, 6.367, 9725);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (615, false, '/pulJ1iY7GVeppMRipiR7ZGDW7EW.jpg', 'en', 'The Passion of the Christ', '마지막 만찬 후에 겟세마니 동산에 올라간 예수는 그 곳에서 사탄의 유혹을 물리친다. 그러나 그 곳에서 예수 그리스도는 유다에게 배신 당해 체포되어 예루살렘으로 끌려온다. 바리새인들은 예수 그리스도를 신성모독죄로 단죄하고, 재판에서 사형을 선고한다.  팔레스타인의 로마 제독, 빌라도는 바리새인들의 주장을 들으며 그의 앞에 끌려온 예수 그리스도를 어떻게 처리할지 고민한다. 자신이 정치적 위기에 직면해 있음을 깨달은 빌라도는 이 문제를 헤롯왕에게 의논한다. 헤롯왕은 빌라도에게 예수 그리스도를 돌려보낸다. 이에 빌라도가 군중들에게 그리스도와 죄수 바라바 중 누구를 석방할지 결정하도록 하자, 군중들은 바라바에게 자유를 예수 그리스도에게 처형을 주장한다.  로마 병사들로부터 처참하게 채찍질을 당한 그리스도는 빌라도 앞에 다시 끌려오게 된다. 빌라도는 만신창이가 된 예수 그리스도를 군중에게 보이며, ‘이 정도면 충분하지 않은가?’라고 묻지만 피에 굶주린 군중들은 만족하지 못한다. 딜레마에 빠진 빌라도는 군중들이 원하는 대로 처리하도록 부하들에게 명령한다.  그리하여 예수 그리스도는 예루살렘 거리를 지나 골고다 언덕까지 십자가를 메고 가도록 명령을 받는다. 골고다 언덕 위에서 예수 그리스도는 십자가에 못 박히게 되고 그 곳에서 마지막 유혹에 직면한다. 그의 아버지가 그를 버렸을 지도 모른다는 두려움 때문이었다. 하지만 예수 그리스도는 두려움을 극복하고 어머니인 마리아를 바라보며 그녀만이 완전히 이해할 수 있는 마지막 한 마디를 하고 죽는다. “다 이루었도다.”“내 영혼을 아버지 손에 맡기나이다.”...', 81.088, '/hNnh0cwtv5iQ1ErTSZXMbo1Q2Rl.jpg', '2004-02-25', '패션 오브 크라이스트', false, 7.504, 4488);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (628, false, '/3fChciF2G1wXHsyTfJD9y7uN6Il.jpg', 'en', 'Interview with the Vampire', '18세기 뉴올리언즈, 카리스마가 넘치고 퇴폐적이고 거칠것이 없는 뱀파이어 레스타트(톰 크루즈)는 루이스(브래드 피트)를 뱀파이어로 만든다. 그 삶은 죽음도 고통도 없이 영원한 젊음으로 이루어져 있으나, 신선한 피를 마시지 못하면 단 하루도 버틸 수 없는 뱀파이어의 삶. 인간적인 마음 때문에 사람의 피를 거부하던 루이는, 어느날 엄마를 잃은 고아 소녀 클로디아를 만나 끌리게 된다. 레스타트는 루이의 마음을 알고 그녀를 또 하나의 뱀파이어로 만들어 셋이서 가족을 이루는데...', 70.529, '/7NNljQFr46lTQbxBj9i3oMXXw7x.jpg', '1994-11-11', '뱀파이어와의 인터뷰', false, 7.387, 5694);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (630, false, '/nRsr98MFztBGm532hCVMGXV6qOp.jpg', 'en', 'The Wizard of Oz', '회오리 바람에 휩쓸려 오즈의 나라로 내던져진 도로시는 집으로 되돌아갈 수 있는 유일한 길이 위대한 오즈의 마법사를 만나는 것임을 알고 그를 찾아 긴 여정이 시작된다. 도로시는 애견 토토와 함께 노란 길을 따라 오즈의 마법사가 사는 에메랄드 시티로 향한다. 도중에 만난 세 명의 친구들, 지능을 얻고자 하는 허수아비와 심장을 원하는 양철 나뭇꾼, 용기를 가지고 싶어하는 겁장이 사자와 함께 오즈의 마법사에게 자신의 소원이 이루어지도록 부탁하기 위해 도로시와 함께 경쾌한 발걸음을 옮긴다. 그러나 도로시 일행을 방해하기 위해 뒤쫓아오는 서쪽 나라 마녀의 검은 그림자.', 81.175, '/oEHkFDJzXL9dLoSymyc5b6DTvDn.jpg', '1939-08-15', '오즈의 마법사', false, 7.6, 5410);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (640, false, '/Ag6qhzsJd3k1NKuNrG9RmhZDMh7.jpg', 'en', 'Catch Me If You Can', '1965년, FBI를 발칵 뒤집는 사건이 발생한다. 파일럿을 가장해 모든 비행기에 무임승차는 기본, 50개 주 은행을 순회하며 무려 140만 달러를 횡령한 희대의 사기꾼이 나타난 것. 21년 경력의 FBI 최고의 요원 칼 핸러티가 프랭크의 뒤를 쫓고, 드디어 프랭크의 호텔방을 덮친다. 하지만 칼과 마주친 프랭크는 정부 비밀요원으로 둔갑, 증거물을 챙긴다며 위조수표를 챙겨 들고 여유롭게 빠져나온다. 뒤늦게 자신이 속았음을 깨달은 칼은 자존심 회복을 결심한다. 그리고 마침내 찾은 프랭크의 가족. 하지만 자신을 속인 프랭크가 겨우 17살의 고등학생이라는 사실을 알게 되는데...', 83.615, '/1q4rV06m6PginBB4nAgYcG48Oik.jpg', '2002-12-16', '캐치 미 이프 유 캔', false, 7.976, 15345);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (646, false, '/ptcXbbxTyjHfEE4d4QocNP0YQct.jpg', 'en', 'Dr. No', '미국의 로켓 발사 실험이 계속 실패하는 상황에서 자메이카 지역에 파견된 스트랭웨이스 교수가 정체불명의 적에게 살해되는 사건이 발생한다. 살해된 스트랭웨이스 교수는 사실 영국의 비밀 첩보기관 MI6의 요원이었고, 이 불가사의한 사건을 수사하기 위해 제임스 본드는 자메이카로 향한다. 단서를 찾아 스트랭웨이스 교수의 집을 찾아간 본드는 책상 위의 사진에서 콰럴이란 이름의 어느 흑인 남성이 교수와 있는 것을 보게된다. 본드는 그를 찾아가고, 그에게 크랩 키 섬에 사는 괴짜 박사에 대한 이야기를 듣고 크랩 키 섬으로 잠입한다.', 74.695, '/9zCOLJmLNst0sCPZlkW1IRoH65E.jpg', '1962-10-07', '007 살인번호', false, 6.997, 3484);
