INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1734, false, '/fRnhnR2JfkGBT19z7EXEmIUcDSo.jpg', 'en', 'The Mummy Returns', '\'미이라\'에서 동고동락을 한 릭 오코넬과 에블린은 결혼하여 8살짜리 아들까지 두고 행복한 가정을 꾸려나가고 있다. 그들은 고대 이집트 문명에 관한 전문가가 되어, 세계 어느 곳이든 발굴 여행을 떠난다.  그러던 어느날 에블린이 환영을 보고, 그것을 근거로 이집트의 한 무덤에 발을 들여 놓는다. 그곳은 바로 오래전 죽음의 신 아누비스의 군대를 이끌고 전세계를 정복하였던 전설적인 왕 스콜피온 킹의 팔찌가 있는 곳이었다. 스콜피온 킹이 세계를 정복하는 꿈을 이루자 죽음의 신은 스콜피온 킹이 약속한대로 그의 영혼을 거두고 그의 군대와 종족을 없애 버렸다. 스콜피온 킹은 누군가가 그의 팔찌를 찾아 그를 환생시켜 군대를 다시 통솔할 날을 기다리며 조용히 무덤속에 묻혀 있었다.  한편, 부활한 고대 이집트 마법사 이모텝은 세계를 정복하기 위해서는 스콜피온 킹 팔찌를 찾은 뒤 그를 죽이고 그의 군대를 이용해야만 한다는 결론을 내린다. 그러나 그 팔찌는 우연히 오코넬 아들 손에 들어가고...', 89.122, '/1mYrlAHF0U99dm3ijaw13EpAx01.jpg', '2001-05-04', '미이라 2', false, 6.375, 6867);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1735, false, '/z1btePhD1jIDLbjy5OCnO0KVf5O.jpg', 'en', 'The Mummy: Tomb of the Dragon Emperor', '기원전 221년, 세계를 정복하려던 황제 한은 여사제의 저주에 묶여 미이라로 땅속에 묻힌다. 이후 2천년이 지나 상하이 박물관으로 유물 인수에 착수한 릭 오코넬(브랜든 프레이저)과 그의 가족은 우연히 황제의 무덤을 발견하게 되고, 황제는 미이라의 힘을 이용하려는 세력의 음모에 의해 깨어나게 된다. 분노로 가득찬 미이라와 그의 테라코타 군사들을 막기 위해 오코넬 가족은 다시 한번 위험한 모험을 시작하게 되는데...', 84.503, '/6ZnbNShjP8Syaq3nZmoSJOOvxZs.jpg', '2008-07-01', '미이라 3: 황제의 무덤', false, 5.494, 4330);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1771, false, '/yFuKvT4Vm3sKHdFY4eG6I4ldAnn.jpg', 'en', 'Captain America: The First Avenger', '진주만 공습을 겪은 미국은 선전포고를 하고 제2차 세계대전에 발을 들인다. 스티브 로저스는 왜소하고 마른 체격으로 인해 입대마저 번번히 거부당하지만, 포기를 모르는 근성과 강한 희생 정신을 인정받아 최고의 전사를 양성하는 슈퍼 솔져 프로젝트에 스카우트된다. 비밀리에 진행된 실험을 통해 가장 완벽한 육체와 인간의 한계를 초월한 신체 능력을 얻게 된 스티브는 모두에게 캡틴으로 불리며 영웅으로 새롭게 태어난다. 하지만 거대한 히드라 조직을 앞세운 적의 공격은 한층 막강해지고, 그 핵심에 선 레드 스컬은 인류를 위협하는 최후의 전투를 준비하는데...', 76.149, '/v37Dq0G5Z8Yx2TLChlzS9Minzo2.jpg', '2011-07-22', '캡틴 아메리카: 퍼스트 어벤져', false, 7.001, 21102);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1813, false, '/i9lR2jLV0dHVfVs1u6QqqbQAE9I.jpg', 'en', 'The Devil\'s Advocate', '64번의 재판에서 한 번도 실패해 본 적이 없는 변호사 케빈 로막스(키아누 리브스). 그는 의뢰인인 피의자의 유죄가 확실했던 이번 재판에서도 승리를 하면서 인기 절정의 변호사로 부상한다.  승소 기념 파티를 벌이던 날, 케빈은 뉴욕 \'존 밀튼 투자회사\'의 직원으로부터 파격적인 조건의 스카우트 제의를 받고 아내 매리앤(샤를리즈 테론)과 함께 뉴욕으로 향한다.', 100.724, '/1rT40EraiopJVxmkS5cCYUmdeYJ.jpg', '1997-10-17', '데블스 에드버킷', false, 7.462, 5825);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1824, false, '/v8lPo8UWKyP2tIXD8N64HfNP4Pw.jpg', 'en', '50 First Dates', '‘헨리’(아담 샌들러)는 낮에는 하와이 수족관에서 동물들을 돌보고,  밤에는 여행객들과의 화끈한 하룻밤을 즐기는 노련한 작업남.  우연히 ‘루시’를 만나게 된 그는 사랑스러운 그녀에게 첫 눈에 반해 다가간다.  그러나 ‘헨리’의 화려한 입담에 넘어온 줄로만 알았던 ‘루시’는  다음 날 그를 파렴치한 취급하며 기억조차 하지 못한다.  ‘헨리’는 그녀가 단기 기억상실증에 걸렸으며,  매일 아침이면 모든 기억이 10월 13일 일요일 교통사고 당일로 돌아가버린다는 사실을 알게 된다.  매일이 자신과의 첫 만남인 ‘루시’의 마음을 사로잡기 위해 ‘헨리’는 매번 기상천외한 작업을 시도하고,  하루 하루 달콤한 첫 데이트를 만들어가던 어느 날,  ‘루시’는 자신이 단기 기억상실증에 걸렸다는 사실을 깨닫고 충격을 받게 되는데...  과연, 두 사람의 사랑은 이뤄질 수 있을까?', 70.591, '/js2Fzu31T3EqTgFbte7YrOXwvV.jpg', '2004-02-13', '첫 키스만 50번째', false, 6.861, 7041);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1825, false, '/36ioS38a5xjITP6qpZg1zDUL5K4.jpg', 'en', 'Over the Top', '탄탄한 체력 하나가 전재산인 트럭 운전사 호크(Lincoln Hawk: 실베스타 스탤론 분)는 소년 군사 학교 졸업식장에서 12살의 아들 마이클(Michael Cutler: 데이비드 멘덴홀 분)를 만난다. 대부호인 커틀러(Jason Cutler: 로버트 로기아 분)는 자기 딸이 호크와 결혼한 것이 못마땅해서 호크를 집에서 몰아냈지만 이제 다시 유일한 핏줄인 외손자 마이클이 호크와 만나는 것이 불안하여 다시 호크를 쫓아내려고 애쓴다.  귀하게 자란 마이클은 뜨내기 트럭 운전사가 아버지라는 사실에 수치심을 느끼지만 시간이 흐를 수록 부자지간의 진한 정을 느낀다. 그러다 병으로 앓던 아내가 죽자, 커틀러는 마이클을 집으로 데려오고, 호크는 접근을 막는 저택의 문을 부수고 들어간다. 곧 체포되고, 할아버지와 사는 것이 더 좋겠다는 마이클의 말에, 양육권을 포기하는 조건으로 석방된다. 호크가 팔씨름 세계 선수권 대회에 나가서 결승에까지 오른다. 하지만 결승 상대가 워낙 강해서 불안을 느낀다. 그러나 집을 빠져나온 아들 마이클의 격려에 자극을 받아 상대를 꺽고 우승하고, 커틀러도 부자의 정을 갈라놓을 수 없음을 깨달아, 호크와 마이클의 행복한 새출발을 시작된다.', 90.122, '/gNkS5YtS59hZP7Zvtw5u9po40rs.jpg', '1987-02-13', '오버 더 톱', false, 6.392, 1618);
