INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1885, false, '/5iMAyVYs4rnQKQ9HDdmuqlb0VtS.jpg', 'en', 'The Karate Kid', '다니엘(Daniel Larusso : 랄프 마치오 분)은 모친 루실르가 직장을 옮기는 바람에 캘리포니아의 소도시로 이사를 간다. 이사간 다음날, 비치 파티에서 앨리(Ali Mills : 엘리자베스 슈 분)라는 여자 친구를 사귀게 되는데 그녀의 애인이었던 자니(Johnny Lawrence : 윌리암 자브카 분)의 멤버들로부터 폭행을 당한다. 이웃집의 일본계 주민, 미야기(Miyagi : 팻 모리타 분)만이 다니엘을 위로해 준다.  괴로운 나날 속에 댄스 파티에서 또 폭행을 당하는 다니엘을 미야기가 당수로 구출하고 \'18세이하 당수 대회\'의 대결로 우열을 가리자고 쟈니와 그 멤버에게 제의, 협약을 맺는다. 다니엘의 피나는 훈련 속에 대회의 날이 다가오는데...', 80.554, '/yxsCYGpPIkicWmallcBqPylHmqF.jpg', '1984-06-22', '베스트 키드', false, 7.19, 4057);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (1891, false, '/aJCtkxLLzkk1pECehVjKHA2lBgw.jpg', 'en', 'The Empire Strikes Back', '제국군은 데스스타를 잃은 뒤에도 여전히 강력한 화력으로 반란군을 수세로 몰아넣었고, 반란군은 게릴라전을 위해 얼음으로 뒤덮인 행성 호스에 은신한다. 하지만 결국 제국군에 의해 호스의 반란군 기지마저 탄로나게 되고, 일행은 필사적으로 호스를 탈출해 다시 정처없는 피난길에 오른다. 반란군의 피난길에 함께 오르던 루크는 포스의 영이 된 오비완으로부터 제다이 마스터 요다를 찾아가라는 메시지를 받고 요다가 있는 정글 행성 대고바로 향한다. 루크는 요다를 만나 제다이가 되기 위한 훈련을 시작하고 솔로 일행은 제국군을 따돌리기 위해 옛 친구 랜도를 찾아가는데...', 79.739, '/icaO5w8uLVm4JxMew71JJrQE2YX.jpg', '1980-05-20', '스타워즈 에피소드 5: 제국의 역습', false, 8.392, 16669);
