INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (4232, false, '/4PPC7fKClu0u7NTbo5xgV4vb5VD.jpg', 'en', 'Scream', '작은 소도시 우즈버러에 사는 케이시에게 이상한 남자로부터 전화가 걸려온다. 비디오를 보려는 케이시에게 그 남자는 케이시의 남자 친구인 스티브의 목숨을 걸고 게임을 하자고 한다. 케이시가 문제를 맞추면 살고, 틀리면 남자친구가 죽는다. 문제는 \'13일의 금요일\'에 나오는 살인마의 이름을 맞추는 것. 케이시는 문제를 풀지 못하고, 스티브는 처참하게 죽임을 당한다. 이튿날, 혼자 집에 있는 시드니에게도 이상한 전화가 걸려온다. 시드니는 케이시와는 달리 힘들게 괴전화의 마수에서 빠져 나온다. 그러나 시드니의 남자 친구 빌리가 살인 용의자로 체포되는데, 조사 후 무혐의로 풀려난다.', 111.294, '/fvprcRjlvrgok1rvOJN4NY8Uqba.jpg', '1996-12-20', '스크림', false, 7.423, 6758);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (4233, false, '/5QRRdnMWtAeu8Zgje2Gdwuo7gd6.jpg', 'en', 'Scream 2', '2년전, 우즈보로를 피로 물들였던 사건을 다룬 게일의 책이 베스트셀러에 진입하면서 시드니 엄마의 살인범으로 기소된 카튼이 석방된다. 게다가 게일의 책이 영화로 만들어져, 첫 시사회를 갖던 중 두명의 대학생이 무참히 살해된다. 또다시 찾아온 공포, 비명소리는 극장에서 다시 시작된다. 끔찍한 악몽을 딛고 이제 대학생으로 평범한 삶을 찾아가던 시드니에게 또다시 전화벨이 울린다. 그녀의 곁에는 공포영화광 랜디, 다정한 남자친구 데릭, 더 큰 특종을 노리며 다가온 리포터 게일, 절룩거리며 끝없이 주위를 배회하는 듀이, 엄마의 살인범으로 기소됐다 풀려난 카튼, 그리고 호기심으로 몰려든 수많은 사람들이 있다. 그러나 시드니를 도우려는 사람들이 많아질수록 비명소리는 커져만 간다. 그들 모두는 희생자일수도 있고, 동시에 용의자일수도 있다. 자신외에는 아무도 믿을 수 없는 시드니, 과연 이 살인극을 벌이는 진범은 누구이며 도대체 무슨 이유로?', 76.966, '/XoIlgmMwbTC4SnmcxJhf8JNFgs.jpg', '1997-12-12', '스크림 2', false, 6.491, 4032);
