INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (8681, false, '/9iQWsXwjOMGDvTDdvCnpiyR0UG3.jpg', 'en', 'Taken', '파리로 여행을 떠난 딸 킴(매기 그레이스)이 아버지 브라이언(리암 니슨)과 통화를 하던 중 납치 당한다. 딸을 찾아 파리에 도착한 브라이언은 킴의 부서진 휴대전화에서 피터의 사진을 발견하고 그를 미행하지만, 결정적인 단서를 얻으려던 순간 피터는 죽고 만다. 마약에 중독된 여성들이 가득한 곳에서 킴이 입고 있던 재킷을 가진 여자를 발견한 브라이언은 그녀를 통해 실마리를 추적한다. 킴이 납치당하던 순간 휴대전화를 향해 소리쳤던 외모를 그대로 지닌 남자를 발견한 브라이언. 마침내 결정적 단서를 얻게 되고, 일생일대의 사투를 시작하는데...', 105.635, '/wUBC2bs4SxGgomacSR32qRm0SPh.jpg', '2008-02-18', '테이큰', false, 7.392, 10970);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (8835, false, '/udQe3ULuZr82E2Pvqpl9CrGt6Fy.jpg', 'en', 'Legally Blonde', '엘르 우즈(리즈 위더스푼 분)는 모두가 부러워하는 아름다운 금발의 소유자이다. 학교에서 남자는 물론 같은 여학생들 사이에서도 인기 만점인 그녀는 장학생이며, 캠퍼스 캘린더의 모델이기도 하다. 거기에 하버드 법대에 다니는 남자 친구 워너(매튜 데이비스 분)가 있어 그야말로 남부러울게 없는 짜릿하고 즐러운 나날을 보내고 있다. 남자 친구 워너(매튜 데이비스 분)가 특별한 저녁을 함께 하자고 요청한 자리에서 워너는 그녀에게 자신은 미래 지향적인 여자를 원한다며 "지나치게 금발(too blonde)"이라는 이유로 이별을 통보한다. 엘르는 비탄에 잠긴다. 하지만 오기가 생긴 엘르, 그녀는 자신은 그가 원하는 사람이 될 수 있음을 증명하려고 결심한다. 그리곤 워너가 다니는 하버드 법대에 들어갈 것을 결심하게 되는데.', 82.885, '/fN3beo2iwZqXGJXYBVJ6t1E5mD2.jpg', '2001-07-13', '금발이 너무해', false, 6.765, 3884);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (8869, false, '/bJO6pHIUvyOeW52G2P5YRpGv5u9.jpg', 'en', 'Eight Legged Freaks', '미국의 작고 외진 한 폐광촌. 산업 폐기물을 싣고 가던 덤프 트럭에서 폐기물통이 떨어져 마을의 강가에 산업폐기물이 노출된다. 인근 부근 거미농장의 주인은 매일마다 강가에서 잡은 귀뚜라미를 거미들에게 공급한다. 귀뚜라미를 먹은 거미들은 며칠사이 2-3배 크기로 급성장을 하게 되고, 급기야 주인을 공격해 목숨을 빼앗는다. 폐기물에 노출되어 비정상적으로 커진 이들 거미떼는 평화로운 광산촌을 덮치게 되고, 마을은 아수라장이 된다. 마침 십년 만에 마을에 돌아온 광산 엔지니어 크리스(데이빗 아퀘트 분)가 옛 애인이자 마을의 보안관인 샘(캐리 뷰러 분)과 손잡고 마을 사람 구조에 앞장선다. 거미떼를 외계인이라고 믿고 있는 케이블 방송국 DJ인 할란(더그 E. 더그 분)과 거미의 생리를 꿰고 있는 샘의 어린 아들 마이크도 적극 동참하지만 무차별적인 거미떼의 습격에 속수무책이다. 암컷의 환심을 사기위해 사람을 산채로 거미줄로 둘둘 말아서 보관하는가 하면, 대형버스를 장난감처럼 흔들어 엎고, 눈깜짝할 사이 타조를 먹어치우는 등 괴물 거미떼들의 거칠고 무차별적인 습격에 아수라장이 된 마을 사람들은 목숨을 부지하기 위해 마을의 대형 마트에 총집결 한다. 통제불능의 거칠고 거대한 거미떼들과 목숨을 건 마을 사람들과의 한판 전쟁이 시작 되는데...', 77.127, '/pnOIkhtOXEDxRwboHsglvcCYQze.jpg', '2002-07-17', '프릭스', false, 5.773, 1190);
