INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (9325, false, '/4TxfSIsjXjbz38Ewb11EKpYPxRd.jpg', 'en', 'The Jungle Book', '벵골호랑이 쉬어칸의 습격으로 부모를 잃은 모글리는 정글에서 조난당하게 된다. 흑표범 바기라가 모글리를 발견하고 늑대 무리에서 갓 새끼를 낳은 라마, 라쿠샤 부부가 모글리를 키우게 된다. 한편 호랑이 쉬어칸이 정글에 돌아왔다는 소문이 퍼지자 늑대들은 모글리의 안전을 위해 그를 인간 세계에 돌려 보내기로 한고, 바기라와 함께 모글리는 긴 여정을 떠난다. 카아라는 굶주린 비단뱀의 유혹으로부터 도망쳐 코끼리 군단속에 휩싸이기도 했던 모글리는 태평스런 곰을 만나 아주 가까운 친구가 된다. 하지만 모글리는 독수리와는 친구이지만 호랑이와는 적인 질투심 많은 원숭이왕에 의해 납치되고 마는데...', 72.149, '/kIeCIEvO0gaihxZIOL5tTXqCZHR.jpg', '1967-10-18', '정글북', false, 7.281, 6125);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (9353, false, '/nNCleqiADKVMKWDC5G7hjYcVCmv.jpg', 'en', 'Nacho Libre', '수도사 이그나쵸는 요리와 환자 심방 외엔 주어지는 일거리가 없는 수도원의 미운 오리다. 어린 시절부터 레슬링을 좋아한 그는 꿈을 이루기 위해 거리에서 만난 에스켈레토와 2인조 팀을 이뤄 레슬링의 세계로 들어선다. 그러나 레슬링을 사악한 것으로 생각하는 수도원의 상황은 수도사인 그를 구속하고, 그의 간절한 바람에도 불구하고 영웅 람세스처럼 뛰어난 프로 레슬러가 될 가능성은 희박해 보인다. 마침내 인기와 명예보다 수도원의 고아들을 위해 레슬러의 길을 걷기로 한 그에게 사랑하는 수녀 엥카나시옹은 남을 도우려고 싸운다면 주님의 축복이 있을 것이라고 말해준다.', 72.838, '/kh7B91bMl2lZ0mH9WhPfaNUIEQH.jpg', '2006-06-16', '나쵸 리브레', false, 6.5, 1560);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (9358, false, '/kDejRs8aKRBr1mARSmDNNZqMW3D.jpg', 'en', 'Final Destination 2', '친구들과 주말여행을 떠나던 킴벌리는 고속도로 연쇄 충돌 사고로 자신을 포함한 많은 사람들이 끔찍하게 죽는 환상을 보게 된다. 환상에서 본 조짐들은 하나하나 현실이 되고 공포에 질린 그녀는 다른 차들의 국도 진입을 막지만 아무도 그 말을 믿으려 하지 않는다. 하지만 예견했던 대형 사고가 실제로 일어나자 이를 지켜보던 사람들은 경악을 금치 못한다. 안도의 한숨도 잠시, 화염에 휩싸인 트럭이 킴벌리의 차를 덮치면서 그녀를 제외한 친구들은 모두 목숨을 잃게 되는데.  단지 이것이 시작일 뿐이라는 것을 직감한 킴벌리는 이번 사고가 1년 전 180기 폭발사고의 생존자들이 겪은 죽음과 연관되어 있음을 알게 되고 180기 사고의 유일한 생존자 클레어를 찾아가 도움을 구한다. 킴벌리와 클레어, 그리고 나머지 생존자들은 다시 시작된 죽음의 맞서 그 해답을 찾으려 하지만 전혀 다른 법칙과 예상치 못한 방법으로 다가오는 죽음은 그들을 더욱 공포에 떨게 만든다 .점점 빠르게 다가오는 공포의 검은 그림자. 과연 그들은 죽음과의 숨막히는 게임에서 살아 남을 수 있을 것인가?', 72.526, '/3OdyCaz3tRJhY4JeZwHd0nG6jNw.jpg', '2003-01-31', '데스티네이션 2', false, 6.2, 3930);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (9471, false, '/7r8X5peJWDoNRolmBVkyHqX1L1K.jpg', 'en', 'Charlie\'s Angels: Full Throttle', '2개의 타이타늄 반지, 초극비 프로젝트 HALO, 그리고 미녀삼총사…  백만장자 찰리를 주축으로 정부를 위해 일하는 매력적인 미녀삼총사, 나탈리, 딜런, 알렉스. 어느날 미 법무성과 연방요원이 관리하던 FBI의 증인보호 프로그램 “HALO”가 담긴 2개의 타이타늄 반지가 연달아 도난당하고, 보호 중이던 증인들을 무차별 살해하는 테러가 발생한다.  더욱 섹시하고 더욱 강해진 그녀들의 임무가 시작된다!  이제 3년만에 새로운 프로젝트에 투입된 미녀삼총사의 임무는 사라진 반지를 되찾고 나머지 증인들의 생명을 보호하는 것!  그러나 섹시하고 터프한 미녀삼총사 앞에 찰리의 옛 부하이자 “타락천사”로 변해버린 매디슨이 나타나 사사건건 그녀들을 방해하는데…', 81.531, '/fCplbYvYDcS3PP026gi07WlRQJg.jpg', '2003-06-25', '미녀 삼총사: 맥시멈 스피드', false, 5.465, 2956);
