INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10144, false, '/wQle1uFv6qKWvv0dk8r5oe6mMP4.jpg', 'en', 'The Little Mermaid', '바다의 왕 트리톤의 막내딸 에리얼은 사랑스럽고 아름다운 목소리를 가져 모든 이들의 사랑을 독차지하고 있다. 인간 세계에 대한 왕성한 호기심을 갖고 있던 에리얼은 어느날 인간 세상 근처에 갔다가 생일 파티 중인 왕자를 보고 한눈에 반한다. 배가 폭풍을 만나 바다에 빠진 왕자를 구한 에리얼은 그를 진심으로 사랑하게 되고 그의 곁에 있고 싶어한다. 그 사실을 알게 된 마녀 우슬라는 에리얼에게 다리를 주는 대신 그녀의 목소리를 가지기로 한다. 3일 안에 왕자의 키스를 받지 못하면 우슬라에게 영혼을 빼앗기기 때문에 친구인 세바스찬과 플라우더는 갖은 방법을 써서라도 왕자와 에리얼이 잘 되도록 하려고 한다.', 152.807, '/dxEZkFIxuLjyS6VEHeirlURHlrt.jpg', '1989-11-17', '인어공주', false, 7.353, 7719);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10159, false, '/oy4NVW7peNLRn0X1DEVz7u6q8NJ.jpg', 'en', 'The Rundown', '자타가 공인하는 실력을 지녔지만 언젠가는 근사한 레스토랑을 여는 게 꿈인 최고의 ‘회수전문가’ 벡(드웨인 존슨). 단 한번의 실패도 없는 경력을 자랑하는 베테랑인 그에게 최고의 위기가 될지도 모르는 의뢰를 받게 된다. 베일에 싸인 보물을 찾겠다고 정글로 간 트라비스(숀 윌리엄 스코트)를 찾아달라는 요청을 수락한 벡은 위험천만의 황금도시 ‘헬도라도’로 떠난다.  생각보다 쉽게 트라비스를 찾아내는데는 성공하지만 신비의 보물 ‘가토’의 실마리를 지닌 신비의 여인 마리아나가 나타나면서 일은 점점 복잡하게 꼬이기 시작한다. 게다가 정글의 독재자 헷쳐(크리스토퍼 월켄)마저 ‘가토’를 노리며 온갖 수단을 동원해 벡과 트라비스를 추적하기 시작하는데...', 76.827, '/mDWZLiTm2k6y0qRmZW8WgmLGvG.jpg', '2003-09-26', '웰컴 투 더 정글', false, 6.451, 1926);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10191, false, '/59vDC1BuEQvti24OMr0ZvtAK6R1.jpg', 'en', 'How to Train Your Dragon', '바이킹의 세계, 절대 친구가 될 수 없는 둘이 만났다?  용맹한 바이킹과 사나운 드래곤들의 싸움이 끊이지 않는 버크섬.  바이킹 족장의 아들 ‘히컵’은 드래곤 사냥에 소질 없는 마을의 사고뭉치. 어느날 그는 부상 당한 드래곤 ‘투슬리스’를 구하게 되고, 아무도 몰래 그를 돌본다. 서로를 알아가며, 드래곤들의 친구가 된 ‘히컵’. 그들과의 새로운 생활을 만끽하던 ‘히컵’은 드래곤들의 위험한 비밀을 알게 되는데…  불멸의 드래곤과 바이킹족 이단아의 만남!  2010년 5월, 그들의 스펙터클한 모험이 시작된다!', 106.826, '/5JtTkNwOeztwfCPv965e9bZi6G3.jpg', '2010-03-18', '드래곤 길들이기', false, 7.829, 12753);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10192, false, '/xZ2we4gdiwQmg6D1w9qHlAm5yIf.jpg', 'en', 'Shrek Forever After', '슈렉이 아름다운 공주 피오나와 겁나먼 왕국을 구해낸 이후. 그들도 오래오래 행복하게 살았...더라면 참 좋았을텐데! 과연 슈렉에게도 모두가 바라던 해피엔딩이 올까?  한 가정의 평범한 아빠와 남편으로 반복되는 일상에 따분함을 느끼던 슈렉은 마을 사람들에게 겁을 주고, 진흙 목욕도 실컷 하던 시절의 자유를 꿈꾸게 된다. ‘단 하루라도 자유로워진다면 죽어도 한이 없겠어’라며 일탈을 꿈꾸던 슈렉은 ‘겁나먼 왕국’을 차지하려는 악당 럼펠의 그럴싸한 계략에 속아 넘어가 ‘완전 딴판 겁나먼 세상’에 떨어지고 만다. 완전 딴판으로 바뀌어버린 세상에서 절친 동키는 슈렉을 미친 괴물 취급하며 줄행랑 치고, 단 하나의 사랑 피오나는 하이킥을 날리고, 장화신은 고양이는 더 이상 장화를 신지 않는 D라인의 고양이가 되어 있다. 친구들과 왕국, 그리고 하나뿐인 진실한 사랑을 되찾기 위한 슈렉의 마지막 이야기가 시작된다!', 144.03, '/t3FNCqPBgRK9jYTzLGWptIdpD3O.jpg', '2010-05-16', '슈렉 포에버', false, 6.377, 7199);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10193, false, '/wE5JGzujfvDPMIfFjJyrhXFjZLc.jpg', 'en', 'Toy Story 3', '모든 장난감들이 겪는 가장 슬픈 일은 바로 주인이 성장해 더이상 자신들과 놀아주지 않는 것. 우디와 버즈에게도 그 위기가 찾아온다. 앤디가 대학에 진학, 집을 떠나게 된 것. 헤어짐의 불안에 떨던 토이들은 앤디 엄마의 실수로 집을 나오게 되고 우여곡절 끝에 탁아소에 기증된다. 앤디가 자신들을 버렸다고 생각하는 장난감들. 게다가 어린이집의 아이들은 난폭하고 험한 데다가, 상상도 못했던 거대한 음모까지 숨겨져 있는 어린이집 장난감의 세계. 앤디가 여전히 자신들을 사랑한다는 사실을 알게 된 장난감들은 앤디 곁으로 돌아가기 위해 생애 가장 큰 모험을 결심하는데...', 124.74, '/hbUWahBLUon8RaIb9Tq7aWCBCtS.jpg', '2010-06-16', '토이 스토리 3', false, 7.799, 14434);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10195, false, '/cDJ61O1STtbWNBwefuqVrRe3d7l.jpg', 'en', 'Thor', '파괴를 일삼는 요툰하임을 제압한 왕 오딘의 통치로 평화로운 나날을 보내고 있는 신의 세계 아스가르드. 오딘의 아들인 토르의 왕위 계승식이 있던 날, 요툰하임의 지배자인 라우페이는 평화 협정을 위반하고 아스가르드를 침입한다. 이 사건으로 왕위에 오르지 못한 토르는 분노와 모욕감에 요툰하임을 공격하지만 막강한 군대에 포위되어 위기에 몰리게 되고, 오딘은 토르의 오만하고 경솔한 행동에 분노해 토르의 신으로서의 능력을 박탈한 채 인간 세계 미스가르드로 추방한다. 한편 천체의 이상현상을 연구하던 과학자 제인 일행은 거대한 섬광의 흔적을 쫓던 중, 그 현장 한가운데서 지구에 떨어진 토르와 맞닥뜨린다.', 156.85, '/f99ngOw3HByxzT85EgUdd7MF8c4.jpg', '2011-04-21', '토르: 천둥의 신', false, 6.77, 20832);
