INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10246, false, '/7zVPeUgL6pgBmV0H9ptqeMiGHUD.jpg', 'en', 'Porky\'s', '엔젤비치고등학교의 소문난 6명은 장난을 좋아하는 단짝 친구들, 그 중 어수룩하지만 유난히 여자를 밝히는 피위는 항상 친구들의 놀림감이 된다. 피위 일당은 여자를 구하러 이웃 마을의 성인 나이트 클럽 포키스에 가지만 포키에게 속아 돈만 빼앗기고 쫓겨난다. 더구나 그 마을 보안관인 윌러스는 포키의 동생으로, 포키와 작당하여 미키의 차를 망가뜨린다. 이를 항의하던 미키는 포키 일당에게 얻어 맞고, 친구들은 복수를 결심, 피위의 형이자 보안관인 테드의 지원을 요청한다. 트럭과 보트를 이용한 작전으로 포키 클럽을 산산히 부순 이들은 포키와 윌러스의 추격을 받으며 마을로 돌아온다. 그러나 마을경계에서 기다리던 테드는 미성년자를 출입시킨 혐의로 포키일당을 꼼짝못하게 하고, 포키 일당은 하는 수 없이 돌아가게 된다.', 103.365, '/AqCOt1cWfKqCepxK7LIdT812idH.jpg', '1981-11-13', '포키스', false, 6.403, 835);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10249, false, '/2fQ6d8B0wgKTr2c7aHcDW8DqwIB.jpg', 'en', 'The Rocketeer', '1938년 로스엔젤레스. 경비행기 조종사인 클리프 씨코드(빌 캠벨 분)는 부적처럼 꼬리 날개에 껌을 붙이고 아름다운 애인 제니 블레이크(제니퍼 코넬리 분)의 사진을 보면서 첫 곡예 시험 비행에 들어간다. 멋진 비행 솜씨를 보이던 클리프는 곧 FBI에 쫓겨 충격전이 벌어지는 곳에 휘말려 비행기 고장으로 추락, 엔지니어인 피비(알란 아킨 분)와 3년간 준비한 전국대회의 우승은 물거품처럼 사라진다. 범인 데니는 죽고 부상당한 나머지 한 명인 윌머(맥스 그로덴킥 분)를 잡은 FBI는 폭발된 자동차에서 자신들이 찾던 \'사이러스 X-3 로켓\' 모양의 파괴된 물체를 발견하여 로켓은 폭발되었다고 로켓 제작자인 하워드 휴즈에게 보고 한다. 그런데 클리프와 피비는 FBI에 쫓기던 범인들이 비행기 격납고의 낡은 비행기 안에 숨긴 로켓 장비를 발견한다. 이상하게 생긴 그 기계의 스위치를 켜자 놀라운 속도로 날아오른다.', 87.088, '/2tDFRESFwKww1LHRE8W1Exj9edH.jpg', '1991-06-21', '인간 로켓티어', false, 6.473, 906);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10307, false, '/t1wXPJuhUXCcv5X2Wl615nmIvtk.jpg', 'en', 'Stigmata', '\'스티그마타(Stigmata)\'는 예수님이 돌아가실 때 입은 상처를 그대로 받은 현상, 즉 성흔을 말한다. 이는 극히 드문 현상으로 옛날부터 믿음이 가득찬 소수의 신자들이 예수가 받은 상처를 받아왔는데, 이러한 징후는 신자들 사이에서는 영광으로 받아들여져 왔다.  어느 날 미용사인 프랭키 페이지(패트리샤 아퀘트)는 어머니가 해외여행에서 선물로 보내준 어느 신부의 묵주를 받은 후 그 징후가 나타나는데, 그녀는 종교가 없는 지극히 평범한 20대 초반의 여자였다. 때와 장소를 가리지 않고 찾아오는 \'성흔\'의 고통에 그녀는 병원으로 실려가 외과는 물론 정신과 치료를 받지만 그 원인에 대해서는 밝혀지지 않았다. 지하철에서 신부가 이를 비디오로 촬영하여 바티칸으로 보내게 되고, 바티칸은 사실 규명을 위하여 교회의 기적을 찾아다니는 신부이자 과학자인 앤드류 키어난(가브리엘 번)을 파견한다. 앤드류 키어난은 그녀의 스티그마타 현상을 통해 예수가 직접 전한 복음이 있다는 사실을 알게 된다. 그 복음 내용은 현 교회체제를 전면적으로 거부하는 어마어마한 내용이었다. 바티칸은 이 내용을 철저히 은폐시켜 왔던 것이다. 바티칸의 음모를 눈치챈 앤드류는 신이 보여주는 초자연적인 힘에 의해 고통을 받는 그녀를 구해야 한다는 생각이 앞서, 자신이 신부임에도 불구하고 바티칸과 충돌을 감행한다', 78.288, '/sKS1Vuqj7OQpOumfs6ArC0TICTN.jpg', '1999-09-02', '스티그마타', false, 6.282, 1057);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10312, false, '/sPGgJ0w1oCVPBOW41dN0hpMouhI.jpg', 'en', 'Patch Adams', '1969년, 헌터 아담스는 불행한 가정환경에서 자라나 자살 미수로 정신병원에 수용된다. 삶의 방향을 잃고 방황하던 그는 정신 병원의 동료환자로부터 영감을 받고 ‘상처를 치유하다’라는 의미의 ‘패치’라는 별명을 얻으면서 ‘패치 아담스’로서 새 인생을 시작한다. 그의 꿈은 사람들의 정신적 상처까지 치료하는 진정한 의사의 길. 2년 후 버지니아 의과대학에 입학한 괴짜 의대생 패치는 3학년이 되어서야 환자를 만날 수 있다는 규칙을 무시하고 빛나는 아이디어와 장난기로 환자들의 마음까지 따뜻하게 치유하려고 환자들을 몰래 만난다. 이 사실을 안 학교측이 몇 번의 경고 조치를 내리지만 그는 아랑곳하지 않고 산 위의 허름한 집을 개조하여 의대생 친구들과 함께 소외되고 가난한 이들을 위한 무료 진료소를 세운다. 그러나 의사면허증 없이 진료행위를 한 것이 학교측에 발각되고 패치와 진실한 사랑을 나누던 동급생 캐린이 정신이상 환자에게 살해당하는 사건까지 생긴다. 인간에게 환멸을 느낀 패치는 모든 것을 포기하고 자포자기 심정에 빠지지만, 생명의 진리를 깨닫고 다시 의사의 길에 의욕을 불태운다. 그러나, 고지식하고 권의적인 윌컷 학과장은 패치에게 퇴학처분을 내리자, 주립의학협회에 제소한다. 위원회는 학칙을 어겼지만, 그의 열정과 학업 성적을 인정, 마침내, 졸업을 하게 된다.', 80.111, '/xN1aKur5ddWQSXTqvzDPJD2TCxe.jpg', '1998-12-25', '패치 아담스', false, 7.308, 3136);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10315, false, '/qU4HDNKv7gjdlvMu74r70rISPwn.jpg', 'en', 'Fantastic Mr. Fox', '12년 전 깨끗하게 손을 씻고, 가정적인 남편이자, 지역 신문 칼럼니스트로 활동하고 있는 Mr. 폭스. 큰맘 먹고 장만한 새집에서 즐기는 평온한 전원 생활은 오히려 그의 잠자고 있던 야생 본능을 깨우고. 급기야 예전 신기의 절도 기술을 활용, 인간 마을 악질 농장주 3인방의 창고를 습격하고 만다. 이에 분노한 농장주들은 Mr. 폭스의 집을 송두리째 파괴해가며 그를 잡기 위해 혈안이 되고, Mr. 폭스와 가족은 물론 이웃들까지도 식량 하나 없는 지하 세계에 갇혀버리는 위험에 처한다. 이제 생존권을 되찾고 동물 사회 전체를 구하기 위한 Mr. 폭스의 판타스틱한 작전이 시작되는데...', 85.903, '/yUM2QsQuLozK81HDIEZJW09fRhy.jpg', '2009-10-23', '판타스틱 Mr. 폭스', false, 7.8, 5270);
