INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10515, false, '/3cyjYtLWCBE1uvWINHFsFnE8LUK.jpg', 'ja', '天空の城ラピュタ', '광산촌 슬랙 계곡의 기계 견습공인 고아 소년 파즈는 어느날 빛이 나는 목걸이를 한 채 하늘에서 떨어지던 소녀시타를 구해준다. 소녀는 대대로 전해져 오던 목걸이(비행석)로 인해 정부군과 해적 도라 일당들에게 쫓기고 있던 신세. 시타가 이들로부터 무사히 도망갈 수 있게 도와주던 중 파즈는 비행석과 하늘에 떠 있는 성 라퓨타가 실제로 존재한다는 사실을 알게 된다. 라퓨타의 존재를 믿고 있던 파즈는 시타와 함께 라퓨타를 찾기로 결심한다. 하지만 파즈와 시타는 그들을 쫓던 군대에게 잡히고, 시타는 정부 비밀 조사관인 무스카에게 파즈를 풀어주는 조건으로 협력을 약속하는데...', 81.974, '/lObRXt53izxDbdlkGpNAHkDACmy.jpg', '1986-08-02', '천공의 성 라퓨타', false, 7.98, 4070);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10527, false, '/r2M9ElkHxEJP7riCFbT5N0uXZTc.jpg', 'en', 'Madagascar: Escape 2 Africa', '마다가스카 섬에서 좌충우돌 모험을 끝낸 뉴요커 4인방 사자 알렉스(벤 스틸러), 얼룩말 마티(크리스 락), 기린 멜먼(데이빗 쉼머), 하마 글로리아(제이다 핀켓 스미스)는 펭귄 특공대가 제작한 비행기 ‘에어 펭귄’을 타고 뉴욕으로 컴백하던 중 또 다시 미지의 세계에 불시착하게 된다. 이번에는 말로만 듣던 진짜 生 야생 ‘아프리카’! 그들은 각자 헤어졌던 가족, 새친구, 연인을 만나 生 야생에 적응해간다. 그러던 중, 평화롭던 아프리카에 가뭄이 찾아오고 뉴요커 4인방은 혼란에 빠진 아프리카를 구하기 위해 뭉치는데…  과연 함께해도 모자란 이들이 위기의 아프리카를 구할 수 있을까?  그리고 아프리카를 탈출해 무사히 집으로 돌아갈 수 있을까?', 107.286, '/9Yk2lAIye5TUBuibD2EYZGYqhz3.jpg', '2008-10-30', '마다가스카 2', false, 6.512, 6748);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10528, false, '/veXdzn7LL0bFIDGmE7tTkvRg0qV.jpg', 'en', 'Sherlock Holmes', '세기의 명탐정_“범죄는 흔하다. 그러나 논리는 흔치 않다” 셜록 홈즈, 천재적인 추리 능력과 주먹의 힘까지 갖추고 친구 왓슨 박사 와 함께 치밀하게 얽힌 미스터리 속에서 진실을 찾아내는 명탐정. 그에게 이제껏 경험하지 못했던 최대의 위협이자 지금껏 그토록 갈구했던 진정한 모험이 몰려오고 있었다. 예고된 살인_“홈즈, 당신과 나는 자연 법칙을 뒤집어 놓을 여정에 올랐소”다섯 명의 여인들이 종교 의식의 제물로 끔찍하게 살해되는 사건이 잇따라 발생하고, 홈즈와 왓슨은 간발의 차이로 마지막 희생자가 될뻔한 여인을 구한다. 범인은 비밀 종교집단 소속의 블랙우드. 붙잡힌 블랙우드는 사형 집행일이 다가올수록 강력한 어둠의 힘을 발휘하고, 자신의 죽음은 계획의 일부에 지나지 않는다며 홈즈에게 경고한다. 죽은 자의 부활_“증거 없는 이론은 위험하네. 사실을 이론에 맞추려 들테니” 블랙우드의 경고는 현실로 나타나고, 죽었던 그의 부활은 도시 전체를 공포 속에 몰아넣는다. 그러나 이것은 홈즈에게는 게임의 시작일 뿐. 블랙우드의 치명적인 음모를 저지하기 위해 홈즈와 왓슨은 고대의 신비한 주술과 현대의 경이로운 신기술이 혼재한 세계로 뛰어든다. 진정한 목적_“가장 사소한 단서가 가장 중요한 증거가 되는 법이지”그러나 홈즈가 해결해야 할 것은 사건만이 아니었다. 그의 앞에 헤어진 연인 아이린 이 등장해 적인지 아닌지 모호한 행동으로 그를 더욱 혼란에 빠뜨린다. 한편, 최강의 콤비 플레이로 사건을 파헤치던 홈즈와 왓슨은 단서들이 공통의 연결고리로 어떤 징후를 나타낸다는 것을 발견하고, 이것이 세상을 파멸시킬 거대한 음모였음을 알게 되는데… 정확한 논리를 무기로, 일격의 주먹을 방어막으로, 세상을 구할 홈즈의 추리가 시작된다!', 83.611, '/57lDlyrA7tWDyscroixKSJRerFU.jpg', '2009-12-23', '셜록 홈즈', false, 7.213, 13707);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10545, false, '/oslaee3VX9kJAjVezbjZKlEJDzn.jpg', 'en', 'The Hunchback of Notre Dame', '흉측한 노틀담의 종지기 콰지모도는 대성당을 나가본 적이 없다. 파리의 영주이자 콰지모도의 주인인 클레드 프롤로는 집시인 콰지모도의 엄마를 죽이고 그것에 대한 속죄로 콰지모도를 키우고 있다. 언제나 대성당에만 있어야 하는 콰지모도, 청년이 된 그는 인간 세상에 나가고 싶어 만우제 가장행렬에 구경나와서 집시인 에스메랄다를 보고 반한다. 그러나 바보들의 왕을 뽑는 행사에서 콰지모도가 왕으로 뽑히고, 처음에 즐거워하던 사람들도 콰지모도가 변장한 것이 아니라 실제 얼굴임을 알고는 콰지모도를 공격하고 에스메랄다는 콰지모도를 구해게 되는데...', 121.711, '/ywa2tsh37i5Sv1Am8Hf3OFadica.jpg', '1996-06-21', '노틀담의 꼽추', false, 7.113, 4884);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10555, false, '/fGuu7bmpM7eFjHcU88SsQdtP5JF.jpg', 'en', 'Shark Tale', '상어 대부 돈 리노와 영웅을 꿈꾸는 오스카의 위험한 정면승부!!  무소불위, 절대권력의 막강 카리스마를 가진 상어 대부 ‘돈 리노’. 하지만 그에게도 말 못할 고민이 있다. 상어의 본성이 의심스러울 정도로 감성적인 아들, ‘레니’가 대부의 아들로서는 자격 미달인 것. 돈 리노는 아들 녀석을 어떻게든 강한 후계자로 키워보려고 하지만, 레니에게는 상어 가문으로서는 차마 밝힐 수 없는 치명적인 비밀이 있다!!  한편, 동네 ‘고래 세차장’에서 일하는 작은 물고기 ‘오스카’는 항상 그럴싸한 말로 위기를 모면하는 비범한 재주(?)의 소유자이다. 그의 유일한 꿈은 ‘부’와 ‘명예’를 얻어 바다 세계 최고 상류 사회로 신분 상승하는 것!!  어느날,‘돈 리노’의 큰 아들이 갑작스레 죽는 사건이 발생하고, 이 엄청난 사건의 장본인(?)은 바로 ‘오스카’로 밝혀진다. 작은 물고기가 상어 대부 큰 아들을 한 방에 보내버렸다는 소식이 전해지자,‘오스카’는 일약 바다 세계 ‘슈퍼스타’로 떠오르고 그토록 바라던 부와 명예 또한 얻게 된다. 그런데 알고보니, 그의 활약상은 사건 현장에서 위기를 모면하기 위해 그가 내뱉은 철없는 거짓말이 영웅담으로 둔갑한 것. 이 사건으로 인해 자존심에 큰 상처를 입게 된 바닷속 대부‘돈 리노’는 가문의 명예 회복과 아들의 복수를 위해 오스카와의 정면 대결을 선포한다.  ‘영웅을 꿈꾸는 작은 물고기, 오스카’와‘상어 대부, 돈 리노’의 자존심을 건 정면 승부, 과연 최후의 승자는 누가 될 것인가? 그리고 예상치 못한 바다 속 이야기는 어떻게 펼쳐질까?', 103.114, '/JHuHF49paaQ9NUWtg32IHIue8X.jpg', '2004-09-20', '샤크', false, 6.031, 6221);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10567, false, '/laeEtakR2pLKu3CeRwB2lU5Y53X.jpg', 'en', 'Dinosaur', '기원전 6500만년 백악기 시대에 카르노타우르스가 이구아노돈(금룡)의 서식지를 습격하여 부화 직전인 이구아노돈 알이 전부 짓밟힌다. 그 알들 중 하나가 우여곡절 끝에 여우원숭이들이 사는 아득히 먼 섬에 떨어진 뒤 알에서 부화하여 알라다라는 이름을 얻어 행복하게 산다. 어느날 거대한 유성이 지구와 충돌하여 섬은 파괴되지만 알라다는 간신히 살아남는다. 그리고 피난 중에 다이너소어 무리와 만나 합류하게 된 알라다는 거기서 니라를 만나 사랑에 빠진다. 카르노타우르스의 공격과 식량이 바닥난 상황에서 다이너소어 무리는 알라다를 중심으로 분열을 극복과 갈등을 극복하고 사랑과 협동으로 위기를 헤쳐나간다.', 97.779, '/8du8bRy6SWGtK4FHgZiUClUJHWT.jpg', '2000-05-19', '다이너소어', false, 6.534, 2442);
INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (10585, false, '/zRBSOPNE00jwomqzpSzC12sHXDz.jpg', 'en', 'Child\'s Play', '흉악 살인범 찰스 리 레이(브래드 듀리프 분)는 형사 마이크 노리스(크리스 서랜든 분)의 추격을 받으며 시카고의 와버쉬 거리 사이로 도주하고 있다. 공범인 에디 카푸토에게 버림받고 부상입은 레이는 닫혀진 장난감 가게로 뛰어든다. 뒤쫓아온 마이크가 쏜 총에 맞고 쓰러진 그는 배신한 친구 에디와 형사 마이크의 복수를 외치면서 \'착한 애(Good Guys)\' 인형에 매달려 주문을 외우자, 밖에서는 불길한 천둥과 폭풍이 몰아쳐면서 가게는 폭발한다.  백화점 점원으로 혼자서 어린 아들 앤디(알렉스 빈센트 분)을 키우고 있는 캐런(캐서린 힉스 분)은 아들의 생일 선물로 인형을 사다준다. 아들 앤디가 그렇게도 원하던 말하는 인형 척키(Chucky: 에드 게일 분)는, 어른들 앞에선 천진스런 인형이다가, 앤디하고만 있게 되면 살아나서 말도 하고 행동도 하며, 더 나아가서는 소름끼치는 살인의 향연을 벌이기 시작한다. 아무도 인형이 살인을 하리라곤 믿지않기 때문에, 의심의 화살은 앤디에게 쏟아지고, 앤디의 힙겹고 외로운 투쟁은 시작된다. 앤디가 척키에 대한 사실을 밝히려 애쓸수록, 그는 이상한 아이로 취급받아 결국 격리당해 정신과 의사의 관찰까지 받게된다.  그러나 캐런이 인형상자에 건전지가 그대로 있는 것을 발견하고, 척키가 건전지 없이 말하고 움직인 것을 알게되자, 척키는 본색을 드러내는데...', 80.105, '/tiGJO2QHEqZcq5cPNGmqViswzpy.jpg', '1988-11-09', '사탄의 인형', false, 6.708, 2873);
