INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (22683, false, '/xkaeirZBfs9nfz2TJoFHxyOLCcC.jpg', 'en', 'Gifted Hands: The Ben Carson Story', '세계 최초로 샴쌍둥이 분리 수술에 성공한 의사로 우리에게 잘 알려진 벤자민 카슨 박사의 이야기.  벤자민 카슨의 어머니는 벤자민이 8살 때 이혼을 한다. 그녀는 벤자민과 커티스 두 아들을 둔 싱글 맘으로 글을 못 읽을 정도로 배움이 짧았지만 청소부 일과 베이비시터를 하면서 두 아들을 훌륭하게 키운다. 항상 낙제 점수를 받아오던 아들 벤자민에게 항상 “넌 남들 하는 것은 무엇이든 할 수 있다. 단지 넌 더 잘 할뿐이다” 라는 믿음을 주었고 6학년이 되면서 벤자민의 성적도 오르기 시작한다. 결국 벤자민은 예일대 의대에 진학하게 되고, 존스 홉킨스 병원에서 이름을 날리는 세계적인 소아외과 의사가 된다. 마침내 벤자민 카슨은 70명의 의료진과 함께 두개골이 붙은 샴쌍둥이를 분리하는 역사적인 수술에 성공하면서 ‘세계 최고의 의사’라는 찬사와 함께 ‘신의 손(Gifted Hands)’라는 별명을 얻게 된다.', 70.494, '/4NxxtiqezjUmUxoDTOjO5FeJvFt.jpg', '2009-02-07', '타고난 재능: 벤 카슨 스토리', false, 7.787, 626);
