INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (98566, false, '/eezsbzYPbYKjjh6E1XHDBNlLynh.jpg', 'en', 'Teenage Mutant Ninja Turtles', '악당 슈레더와 그의 조직 ‘풋 클랜’이 장악해버린 후 범죄와 폭력이 난무하는 뉴욕시.  열혈 방송기자 에이프릴은 카메라맨 번과 함께 그들의 음모를 파헤치기 위해 나서고,  슈레더와 손 잡은 사업가 에릭 삭스의 정체가 점점 드러나게 된다.  한편, 하수구에 살던 ‘닌자터틀’ 레오나르도, 도나텔로, 라파엘, 미켈란젤로는  암흑으로 변해가는 도시를 구하기 위해 거리로 나오게 되는데....', 113.023, '/4OCFADR8elWg9T8QcIxImnXkwSw.jpg', '2014-08-07', '닌자터틀', false, 5.918, 6672);
