INSERT INTO movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (265195, false, '/7SB3a0TzpkI7yvEBTHb4T1EYRXd.jpg', 'es', 'Relatos salvajes', '( 웰컴 투 땅콩항공 )  한 비행기에 탄 승객들, 모처럼의 여행으로 들떠 있는 이들에겐 한가지 공통점이 있다. 모두가 한 사람을 알고 있다는 것! 그는 누구이며 왜 이들은 한 비행기를 타게 되었는지 밝혀지는데…  ( 원수는 식당에서 )  식당에 찾아온 한 손님. 웨이트리스는 그 사람이 아버지의 원수였던 남자인걸 알고 어쩔 줄 모른다. 식당 주방장은 그녀에게 음식에 쥐약을 넣자고 제안하는데…  ( 분노의 질주 18 )  한적한 도로에서 아우디를 몰던 ‘마리오’는 깐죽대며 추월을 방해하는 고물차 운전자에게 열이 받는다. 욕을 날리며 고물차를 추월하지만 잠시 후 타이어가 펑크 나 그것을 교체하는 사이 그의 앞으로 고물차가 오는데…  ( 합법주차 불법견인 )  자신의 차가 견인되어 억울한 남자. 불법주차가 아니었다고 아무리 공무원에게 항의를 해 보지만 전혀 들어주지 않자 그는 점점 화가 나는데… 과연, 그가 공무원에게 대항하기 위해 취한 행동은?  ( 뺑소니의 최후 )  뺑소니 사고를 친 말썽꾼 아들 때문에 아침부터 정신이 없는 아버지는 경찰이 오기 전에 모든 것을 해결하기 위해 고군분투한다. 그는 결국 집안 정원사에게 대신 죄를 뒤집어써달라는 거래를 제안하게 되는데…  ( 이판사판 결혼식 )  결혼식 날, 너무 행복한 신부! 그러나 그녀는 신랑의 친구 중 한 여인을 의심하기 시작한다. 결국 그녀는 신랑이 자신과 사귀던 중 바람을 핀 사실을 알게 된다. 점점 분노는 커지고 그녀는 핀트가 나가기 시작하는데…', 82.569, '/k6DLpZaRW3i4rDmTe2M6jffYyX7.jpg', '2014-08-21', '와일드 테일즈: 참을 수 없는 순간', false, 7.871, 3291);
