INSERT INTO film_fly.movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (150540, false, '/j29ekbcLpBvxnGk6LjdTc2EI5SA.jpg', 'en', 'Inside Out', '모든 사람의 머릿속에 존재하는 감정 컨트롤 본부. 그곳에서 불철주야 열심히 일하는 기쁨, 슬픔, 버럭, 까칠, 소심 다섯 감정들. 정든 옛 동네를 떠나 도시로 이사하는 라일리 가족. 이사 후 새로운 환경에 적응해야 하는 라일리를 위해 그 어느 때 보다 바쁘게 감정의 신호를 보내지만 우연한 실수로 기쁨과 슬픔이 본부를 이탈하게 되자 라일리의 마음 속에 큰 변화가 찾아온다. 라일리가 예전의 모습을 되찾기 위해서는 기쁨과 슬픔이 본부로 돌아가야만 한다. 그러나 엄청난 기억들이 저장되어 있는 머릿속 세계에서 본부까지 가는 길은 험난하기만 한데...', 463.412, '/qVdrpBY920kKikUmPm89wig60Kd.jpg', '2015-06-17', '인사이드 아웃', false, 7.914, 21345);
INSERT INTO film_fly.movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (263115, false, '/9X7YweCJw3q8Mcf6GadxReFEksM.jpg', 'en', 'Logan', '2029년 알 수 없는 이유로 돌연변이들이 태어나지 않는 미래, 더이상 엑스맨은 없다. 갈수록 재생력이 약해져 늙고 수척해진 로건은 멕시코 국경지대 은신처에 찰스 교수를 보호 중이다. 뇌에 심각한 손상을 입은 찰스 교수는 과거를 제대로 기억하지 못하는 데다 간헐적으로 주변을 위협하는 발작까지 일으킨다. 찰스 교수와 함께 떠날 배를 사기 위해 리무진 기사로 일하며 돈을 모으는 로건. 어느 날 낯선 여인이 찾아와 돌연변이 소녀 로라를 캐나다 국경까지 데려다 달라고 부탁한다. 로건은 로라를 쫓는 기업의 경호집단을 피해 험난한 여정을 떠난다.', 229.488, '/tE6tvHXlAZ8hqM4k9ZvtWtBk4pc.jpg', '2017-02-28', '로건', false, 7.8, 18912);
INSERT INTO film_fly.movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (280180, false, '/rrwt0u1rW685u9bJ9ougg5HJEHC.jpg', 'en', 'Beverly Hills Cop: Axel F', '잊지 못할 비버리 힐스에서의 첫 사건 후 어느덧 40년. 디트로이트 형사 액셀 폴리가 자신의 특기를 발휘하러 다시 돌아온다. 범죄를 해결하러, 물론 말썽도 일으키면서.', 520.013, '/eiAeSJZiVrjytYSi0cdBTZCNA13.jpg', '2024-06-20', '비버리 힐스 캅: 액셀 F', false, 6.867, 766);
