INSERT INTO film_fly.movie (id, adult, backdrop_path, original_language, original_title, overview, popularity, poster_path, release_date, title, video, vote_average, vote_count) VALUES (22, false, '/wW7Wt5bXzPy4VOEE4LTIUDyDgBo.jpg', 'en', 'Pirates of the Caribbean: The Curse of the Black Pearl', '잭 스패로우는 10년 전의 반란 사건으로 자신의 배 블랙펄을 빼앗긴 해적 선장이다. 혼자 대양을 떠돌던 그는 자메이카의 로열포트에 이르러 유령선처럼 변해버린 블랙펄과 재회하게 된다. 아즈텍의 황금을 훔친 블랙펄의 선원들은 영원히 죽지 못하는 저주에 걸린 처지. 보물을 모두 제자리에 돌려놓고 피의 제물을 바쳐야만 고대 신들이 내린 저주를 풀 수 있다. 그들은 로열포트를 습격해 아즈텍의 마지막 목걸이를 가진 총독의 딸 엘리자베스를 납치하고, 엘리자베스를 사랑하는 어린 시절 친구 윌과 스패로우는 그 뒤를 쫓는데...', 178.866, '/3ovejwQO4fKOWx4VgGBJeT8CKCn.jpg', '2003-07-09', '캐리비안의 해적: 블랙펄의 저주', false, 7.804, 20230);
